    type: "POST",

    url: "php/login.php/",

    data: { opcode: 3 },

    success: function( response ) {

      const years = JSON.parse( response );

      for ( let i = 0; i < years.length; i++ ) {

        // eslint-disable-next-line computed-property-spacing, space-in-parens

