$( document ).on( "click", ".tag", function( e ) {

    selectTag( this );

    e.stopImmediatePropagation();

  } );
function selectTag( t ) {

    if ( !t.classList.contains( "selected" ) ) {
  
      tags[ t.id ] = 1;
  
      t.classList.toggle( "selected" );
  
    } else {
  
      delete tags[ t.id ];
  
      t.classList.toggle( "selected" );
  
    }
  
    console.log( tags );
  
  }
